const agora = new Date();
const menos10 = new Date(agora.getTime() - 10 * 1000);
const timestampBruto = 1744238421;
const dataConvertida = new Date(timestampBruto * 1000);

return [
  {
    json: {
      msgTimestamp: timestampBruto,
      convertida: dataConvertida.toISOString(),
      agora: agora.toISOString(),
      menos10: menos10.toISOString(),
      isAfter: dataConvertida > menos10
    }
  }
];
